    const fetchCampaigns = async () => {
      const results = await factory.methods.getDeployedCampaigns().call();
      console.log(results);
      setCampaigns(results);
    };
    fetchCampaigns();
  }, []);

  return <h1>This is root..{campaigns}</h1>;
}

export { Root };
